from pybricks.hubs import PrimeHub
from pybricks.parameters import Direction, Port, Stop
from pybricks.pupdevices import Motor
from pybricks.robotics import DriveBase

# Set up all devices.
prime_hub = PrimeHub()
left_arm = Motor(Port.A, Direction.CLOCKWISE)
right_arm = Motor(Port.B, Direction.CLOCKWISE)
left = Motor(Port.E, Direction.COUNTERCLOCKWISE)
right = Motor(Port.F, Direction.CLOCKWISE)
base = DriveBase(left, right, 55.4, 110.4)

def Mission_2():
    # Map Reveal
    base.straight(700)
    base.turn(-45)
    base.straight(180)
    base.straight(-40)
    right_arm.run_angle(250, -90)
    base.settings(straight_speed=600)
    base.straight(-100, then=Stop.NONE)
    base.arc(-100, angle=-90, then=Stop.NONE)
    base.straight(-600)


# The main program starts here.
base.use_gyro(True)
base.settings(straight_speed=300)
Mission_2()
