from pybricks.hubs import PrimeHub
from pybricks.parameters import Direction, Port, Stop
from pybricks.pupdevices import Motor
from pybricks.robotics import DriveBase
from pybricks.tools import wait

# Set up all devices.
prime_hub = PrimeHub()
right_arm = Motor(Port.B, Direction.CLOCKWISE)
left_arm = Motor(Port.A, Direction.CLOCKWISE)
left = Motor(Port.E, Direction.COUNTERCLOCKWISE)
right = Motor(Port.F, Direction.CLOCKWISE)
robot = DriveBase(left, right, 55.4, 110.4)

def Starting_Position():
    left.reset_angle(0)
    right.reset_angle(0)
    left_arm.reset_angle(0)
    right_arm.reset_angle(0)
    robot.use_gyro(True)
    wait(500)
    robot.settings(straight_speed=300)

def Mission_8():
    # Silo
    robot.straight(335)
    right_arm.control.limits(acceleration=14000)
    for count in range(3):
        right_arm.run_angle(1200, 190)
        right_arm.run_angle(-500, 190)

def Mission_6():
    # Forge
    robot.arc(-500, angle=35)
    robot.turn(60)
    right_arm.control.limits(acceleration=2000)
    right_arm.run_angle(500, 175)
    robot.turn(-35)
    right_arm.run_angle(-500, 175)

def Mission_7():
    # Heavy Lifting
    robot.turn(62)
    robot.straight(50, then=Stop.BRAKE)
    wait(200)
    left_arm.run(-200)
    wait(500)
    left_arm.brake()
    wait(200)
    robot.settings(straight_speed=100)
    robot.settings(straight_acceleration=100)
    robot.straight(75)
    wait(200)
    left_arm.run_angle(-200, -25, Stop.BRAKE)
    left_arm.run_angle(-200, -25, Stop.BRAKE)
    left_arm.run_angle(-200, -25, Stop.BRAKE)
    robot.settings(straight_speed=300)
    robot.settings(straight_acceleration=750)

def Mission_5():
    # Who Lived Here?
    robot.straight(-100)
    robot.turn(-67)
    robot.straight(95)
    robot.drive(0, -45)
    wait(1000)
    robot.brake()


# The main program starts here.
Starting_Position()
Mission_8()
Mission_6()
Mission_7()
Mission_5()
# Drive Back
robot.turn(50)
robot.settings(straight_speed=600)
robot.straight(-200, then=Stop.NONE)
robot.arc(300, angle=-45, then=Stop.NONE)
robot.straight(-300, then=Stop.BRAKE)
