from pybricks.hubs import PrimeHub
from pybricks.parameters import Direction, Port, Stop
from pybricks.pupdevices import Motor
from pybricks.robotics import DriveBase
from pybricks.tools import wait

# Set up all devices.
prime_hub = PrimeHub()
right_arm = Motor(Port.B, Direction.CLOCKWISE)
left_arm = Motor(Port.A, Direction.CLOCKWISE)
left = Motor(Port.E, Direction.COUNTERCLOCKWISE)
right = Motor(Port.F, Direction.CLOCKWISE)
robot = DriveBase(left, right, 55.4, 110.4)

def Mission_8():
    # Mission 8
    robot.straight(360)
    right_arm.control.limits(acceleration=8000)
    for count in range(3):
        right_arm.run_angle(1000, 190)
        right_arm.run_angle(-500, 190)

def Mission_5():
    robot.straight(95)
    robot.drive(0, -200)
    wait(500)
    robot.brake()
    robot.turn(60)
    robot.straight(-150, then=Stop.NONE)
    robot.arc(600, angle=-90, then=Stop.BRAKE)

def Mission_6():
    robot.arc(-500, angle=35)
    robot.turn(60)
    right_arm.control.limits(acceleration=2000)
    right_arm.run_angle(500, 175)
    robot.turn(-35)
    right_arm.run_angle(-500, 175)

def Mission_6_2():
    robot.arc(-500, angle=35)
    robot.turn(60)
    right_arm.control.limits(acceleration=2000)
    right_arm.run_angle(500, 175)
    robot.turn(-35)
    right_arm.run_angle(-500, 175)
    robot.straight(125)
    robot.drive(0, -45)
    wait(1000)
    robot.brake()

def Mission_5_1():
    robot.straight(-100)
    robot.turn(-65)
    robot.straight(95)
    robot.drive(0, -45)
    wait(1000)
    robot.brake()

def Mission_x():
    robot.turn(60)
    robot.straight(100)
    left_arm.run(-200)
    wait(500)
    left_arm.brake()
    robot.straight(50)
    left_arm.run_angle(-200, -25)
    left_arm.run_angle(-200, -25, Stop.BRAKE)
    left_arm.run_angle(-200, -25, Stop.BRAKE)


# The main program starts here.
print(prime_hub.battery.current())
robot.use_gyro(True)
robot.settings(straight_speed=300)
Mission_8()
Mission_6()
Mission_x()
Mission_5_1()
robot.turn(50)
robot.straight(-800)
