from pybricks.hubs import PrimeHub
from pybricks.parameters import Direction, Port, Stop
from pybricks.pupdevices import Motor
from pybricks.robotics import DriveBase
from pybricks.tools import wait

# Set up all devices.
prime_hub = PrimeHub()
right_arm = Motor(Port.B, Direction.CLOCKWISE)
left_arm = Motor(Port.A, Direction.CLOCKWISE)
left = Motor(Port.E, Direction.COUNTERCLOCKWISE)
right = Motor(Port.F, Direction.CLOCKWISE)
robot = DriveBase(left, right, 55.4, 110.4)

def Starting_Position():
    left.reset_angle(0)
    right.reset_angle(0)
    left_arm.reset_angle(0)
    right_arm.reset_angle(0)
    robot.use_gyro(True)
    wait(500)
    robot.settings(straight_speed=300)

def Mission_9():
    # What's on Sale?
    robot.straight(225)
    robot.turn(-62)
    robot.straight(175)
    right_arm.run_angle(500, 195, Stop.BRAKE)
    robot.turn(-33)
    right_arm.run_angle(500, -200)

def Mission_13():
    robot.straight(500)
    # Describe your program.
    robot.turn(45)
    left_arm.run(-500)
    wait(350)
    left_arm.stop()
    robot.straight(250)
    left_arm.control.limits(acceleration=8000)
    left_arm.run_angle(2000, 120)
    # Describe your program.
    robot.straight(-150)
    robot.turn(50)
    robot.straight(250)
    robot.turn(-45)
    left_arm.run(-500)
    wait(350)
    left_arm.stop()
    robot.straight(225)
    left_arm.control.limits(acceleration=300)
    left_arm.run_angle(100, 90)


# The main program starts here.
Starting_Position()
Mission_9()
robot.turn(140)
robot.straight(350)
robot.turn(45)
robot.settings(straight_speed=600)
robot.settings(straight_acceleration=1500)
robot.straight(200)
robot.straight(150)
robot.settings(straight_speed=300)
robot.settings(straight_acceleration=750)
robot.straight(-190)
right_arm.control.limits(acceleration=3000)
right_arm.run_angle(1000, 200)
right_arm.run_angle(1000, -200)
robot.turn(60)
robot.straight(550)
robot.turn(-153)
robot.straight(110)
right_arm.run(500)
wait(600)
right_arm.brake()
wait(200)
robot.settings(turn_rate=800)
robot.settings(turn_acceleration=1500)
# Drive Back
robot.settings(straight_speed=600)
robot.turn(45)
robot.straight(-1100)
